function genEntitiesPage(pageNum){
    var out_msgs = [ {payload:"pageType,cardEntities"}, { payload: "entityUpdHeading,"+pages[pageNum].heading } ]

    pages[pageNum].items.forEach(function (id, i) {

        var type = "delete"
        var iconId = 0
        var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")

        switch (id.substring(0,id.indexOf('.'))) {
            case "cover":
                type = "shutter"
                iconId = 0
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type})
                break
            case "light":
                type = "light"
                iconId = 1
                var optVal = "0"
                if(global.get("homeassistant.homeAssistant.states['"+id+"'].state") == "on")
                    optVal = "1"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "sensor":
                type = "text"
                if(global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.device_class") == "temperature"){
                    iconId = 2
                }
                var optVal = global.get("homeassistant.homeAssistant.states['"+id+"'].state") + " " + global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.unit_of_measurement")
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "button":
                type = "button"
                iconId = 3
                var optVal = "PRESS"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "delete":
                type = "delete"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+",0,dc,"+type})
                break
            default:
                break
        }

    })



    return out_msgs

}

function genThermoPage(pageNum){
