    let status      = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.hvac_action")
    let minTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.min_temp")*10
    let maxTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.max_temp")*10
    //let stepTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.target_temp_step")*10
    let stepTemp     = 0.5*10

