function handleButtonEvent(pageNum, words){
    var out_msgs = [ ]
    pages[pageNum].items.forEach(function (id, i) {
        var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")
        if(words[4]==name){
            if(words[6]=="OnOff"){
                var domain = "switch"
                if(id.startsWith("light"))
                    domain = "light"
                var action = "turn_off"
                if(words[7]=="1")
                    action = "turn_on"
                out_msgs.push( {payload: id, action: action, domain: domain})
            }

            if(words[6]=="up")
                out_msgs.push( {payload: id, action: "open_cover", domain: "cover"})
            if(words[6]=="stop")
                out_msgs.push( {payload: id, action: "stop_cover", domain: "cover"})
            if(words[6]=="down")
                out_msgs.push( {payload: id, action: "close_cover", domain: "cover"})

            if(words[6]=="brightnessSlider")
                out_msgs.push( {payload: id, action: "turn_on", domain: "lightBrightness", brightness: parseInt(words[7])})
            if(words[6]=="colorTempSlider")
                out_msgs.push( {payload: id, action: "turn_on", domain: "lightTemperature", temperature: parseInt(words[7])})

        }
    }

    )
    return out_msgs
}

function searchIdInPages(friendlyName){
    let id_ret = ""
    // search entity in pages
    pages.forEach(page => {
        if("items" in page){
            page.items.forEach(function (id, i) {
                var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")
                if(name == friendlyName){
                    id_ret = id
                }
            })
        }
    })
    return id_ret
}

function scale(number, inMin, inMax, outMin, outMax) {
    return (number - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
}

